        &lt;/style&gt;
    &lt;/head&gt;
    &lt;body&gt;
        &lt;h1&gt;GeoServer + OpenLayers&lt;/h1&gt;

        &lt;div id="map"&gt;&lt;/div&gt;
        &lt;div&gt;&lt;img id="legend" /&gt;&lt;/div&gt;

        &lt;!-- OpenLayers JS--&gt;
        &lt;script src="https://cdn.jsdelivr.net/gh/openlayers/openlayers.github.io@main/dist/en/v7.1.0/ol/dist/ol.js"&gt;&lt;/script&gt;

